import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Navbar from "./components/Navbar";
import Home from "./pages/Home";
import Login from "./pages/Login";
import Signup from "./pages/Signup";
import SongCard from "./components/SongCard"; // This is the SongList component
import Playlist from "./components/Playlist";
import Search from "./components/Search";
import Categories from "./pages/Categories";
import About from "./pages/About";
// NEW: Import the Music Player Context Provider
import { MusicPlayerProvider } from "./context/MusicPlayerContext"; 
// Assuming you have an AuthProvider in context/AuthContext.jsx
// import { AuthProvider } from "./context/AuthContext"; 

export default function App() {
  return (
    // 1. Wrap the entire application with the MusicPlayerProvider
    // The player will now persist across all routes.
    <MusicPlayerProvider> 
      <Router>
        {/* <AuthProvider>  Uncomment this if you wrap your App with AuthProvider */}
          <Navbar />
          {/* Add a main element with padding to ensure the persistent footer doesn't hide content */}
          <main style={{ minHeight: '100vh', paddingBottom: '120px' }}>
            <Routes>
              <Route path="/" element={<Home />} />
              <Route path="/login" element={<Login />} />
              <Route path="/signup" element={<Signup />} />
              <Route path="/songcard" element={<SongCard />} /> 
              <Route path="/playlist" element={<Playlist />} />
              <Route path="/search" element={<Search />} />
              <Route path="/categories" element={<Categories />} />
              <Route path="/about" element={<About />} />
            </Routes>
          </main>
        {/* </AuthProvider> */}
      </Router>
    </MusicPlayerProvider>
  );
}
