import React from 'react';
import { useMusicPlayer } from '../context/MusicPlayerContext';
import './SongCard.css'; // your CSS that contains .player-footer, etc.

export default function MusicPlayer() {
  const {
    activeSong,
    isPlaying,
    togglePlayPause,
    playNext,
    playPrevious,
    currentTime,
    duration,
    handleSeek,
    handleAddToPlaylist
  } = useMusicPlayer();

  if (!activeSong) {
    return (
      <div className="player-footer" style={{ justifyContent: 'center', color: '#b3b3b3' }}>
        Select a song to start playing.
      </div>
    );
  }

  const formatTime = (time) => {
    if (isNaN(time) || time < 0) return '0:00';
    const minutes = Math.floor(time / 60);
    const seconds = Math.floor(time % 60);
    return `${minutes}:${seconds < 10 ? '0' : ''}${seconds}`;
  };

  return (
    <div className="player-footer">
      {/* --- 1. Left: Song Info --- */}
      <div className="player-info">
        <img src={activeSong.cover} alt={activeSong.songname} className="player-cover" />
        <div>
          <h4>{activeSong.songname}</h4>
          <p>{activeSong.artist}</p>
        </div>
      </div>

      {/* --- 2. Center: Controls + Progress --- */}
      <div className="progress-container">
        {/* Player Controls */}
        <div className="player-controls">
          <button onClick={playPrevious} aria-label="Previous Track">⏮️</button>
          <button onClick={togglePlayPause} aria-label={isPlaying ? 'Pause' : 'Play'}>
            {isPlaying ? '⏸️' : '▶️'}
          </button>
          <button onClick={playNext} aria-label="Next Track">⏭️</button>
        </div>

        {/* Progress Bar */}
        <div className="progress-bar-wrapper">
          <span className="progress-time">{formatTime(currentTime)}</span>
          <input
            type="range"
            className="progress-bar"
            min="0"
            max={duration || 0}
            value={currentTime || 0}
            onChange={(e) => handleSeek(e.target.value)}
          />
          <span className="progress-time">{formatTime(duration)}</span>
        </div>
      </div>

      {/* --- 3. Right: Add to Playlist --- */}
      <div className="player-options-right">
        <button
          className="add-to-playlist-btn"
          onClick={handleAddToPlaylist}
          aria-label="Add to Playlist"
        >
          +
        </button>
      </div>
    </div>
  );
}
