import React, { useEffect } from "react";
import "./SongCard.css";
// NEW: Import the custom hook
import { useMusicPlayer } from '../context/MusicPlayerContext'; 

// Removed: formatTime helper function
// Removed: All state (songs, activeSong, isPlaying, currentTime, etc.)
// Removed: All ref (audioRef)
// Removed: All playback functions (skipTrack, handleTimeUpdate, togglePlayPause, etc.)

export default function SongCard() {
    // 1. Use the context to get state and setter functions
    const { songs, activeSong, isPlaying, playSong, setSongs } = useMusicPlayer();
    
    // --- Data Fetching (Keep this here) ---
    useEffect(() => {
        fetch("/songs.json")
          .then((res) => res.json())
          .then((data) => {
            const sortedSongs = data.sort((a, b) => a.id - b.id);
            // 2. Update the global song list state in the context
            setSongs(sortedSongs); 
          })
          .catch((err) => console.error("Error fetching songs:", err));
    }, [setSongs]); // Dependency array should include setSongs

    // 3. The return is now much cleaner
    return (
        <>
            <div className="song-list">
                {songs.map((song) => (
                    <div 
                        key={song.id} 
                        className={`song-card ${activeSong?.id === song.id && isPlaying ? 'is-playing' : ''}`}
                        // 4. Use the global playSong function
                        onClick={() => playSong(song)}
                    >
                        <img src={song.cover} alt={song.songname} className="cover" />
                        <h4>{song.songname}</h4>
                        <p>{song.artist.split(',')[0]}</p>
                    </div>
                ))}
            </div>

            {/* 5. The entire player JSX is GONE from here! 
                It is now rendered in MusicPlayerContext.jsx.
            */}
        </>
    );
}
