import React, { useState, useEffect, useMemo } from "react";
import { useNavigate } from "react-router-dom"; 
import "./Categories.css"; 
import "../components/Search.css"; // Reuse input styling for the search input

// A default cover image for categories that might not have enough songs
const DEFAULT_COVER = "https://placehold.co/100x100/303030/ffffff?text=Music";

// --- Configuration for all Category/Playlist Cards (Finalized with Emojis) ---
const CATEGORIES_CONFIG = [
    // Languages
    { id: "tamil", title: "🇮🇳 Top Tamil Songs", filter: (song) => song.language?.toLowerCase().includes('tamil') },
    { id: "english", title: "🌍 International English Hits", filter: (song) => song.language?.toLowerCase().includes('english') },
    { id: "hindi", title: "🎼 Bollywood Hindi Hits", filter: (song) => song.language?.toLowerCase().includes('hindi') },
    
    // Playlists/Moods/Genres
    { id: "trending", title: "🔥 Trending Now Package", filter: (song) => song.songvariety?.includes('trending') },
    { id: "mass", title: "💥 Mass/Kuthu Hits", filter: (song) => song.songvariety?.includes('mass') },
    { id: "love", title: "💖 Pure Love Songs", filter: (song) => song.songvariety?.includes('romantic') || song.songvariety?.includes('love') },
    { id: "sad", title: "😢 Sad/Melody", filter: (song) => song.songvariety?.includes('sad') || song.songvariety?.includes('melody') },
    // Using genre filter for 'feel good'
    { id: "feel good", title: "✨ Feel Good", filter: (song) => song.genre?.toLowerCase().includes('feel good') }, 
    { id: "vibe", title: "🧘 Vibe/Chill Songs", filter: (song) => song.songvariety?.includes('vibe') || song.songvariety?.includes('chill') },
    { id: "party", title: "🥳 Party Starters", filter: (song) => song.songvariety?.includes('party') },
    { id: "dance", title: "💃 Dance Floor Hits", filter: (song) => song.songvariety?.includes('dance') },

    // Artist Playlists 
    { id: "anirudh", title: "🎤 Anirudh Ravichander Playlist", filter: (song) => song.artist?.includes('Anirudh Ravichander') },
   { id: "sidsriram", title: "🎶 Sid Sriram Collection", filter: (song) => song.artist?.includes('Sid Sriram') },
   { id: "", title: "🎶 ", filter: (song) => song.artist?.includes('chitra') },
   { id: "", title: "🎶 ", filter: (song) => song.artist?.includes('hiphop tamila') },
   { id: "", title: "🎶 ", filter: (song) => song.artist?.includes('chitra') },
   { id: "", title: "🎶 ", filter: (song) => song.artist?.includes('Shreya Ghoshal') },
   { id: "", title: "🎶 ", filter: (song) => song.artist?.includes('chitra') },
   { id: "", title: "🎶 ", filter: (song) => song.artist?.includes('chitra') },


    // Using genre filter for 'peppy'
    { id: "", title: "", filter: (song) => song.genre?.toLowerCase().includes('peppy') },
    // Using genre filter for 'romantic'
    { id: "", title: "", filter: (song) => song.genre?.toLowerCase().includes('romantic') },
    // Using genre filter for 'instrumental'
    { id: "classical", title: "🎻 Classical Instrumental", filter: (song) => song.genre?.toLowerCase().includes('instrumental') },
];

// --- Category Card Component (UNCHANGED) ---
const CategoryCard = React.memo(({ category, covers, onSelect }) => {
    return (
        <div 
            className="category-card" 
            onClick={() => onSelect(category)}
            aria-label={`View songs in ${category.title}`}
        >
            <div className="category-images">
                {Array.from({ length: 4 }).map((_, i) => (
                    <img 
                        key={i} 
                        src={covers[i] || DEFAULT_COVER} 
                        alt={`${category.title} album cover ${i + 1}`} 
                        onError={(e) => { e.target.onerror = null; e.target.src = DEFAULT_COVER; }}
                    />
                ))}
            </div>
            <h3>{category.title}</h3>
        </div>
    );
});


export default function Categories() {
    const [allSongs, setAllSongs] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [searchQuery, setSearchQuery] = useState(''); 
    const navigate = useNavigate(); 

    // Fetch all songs once from /songs.json (Correct Implementation)
    useEffect(() => { 
        setIsLoading(true);
        fetch("/songs.json") 
          .then((res) => {
                if (!res.ok) {
                    throw new Error(`HTTP error! status: ${res.status}`);
                }
                return res.json();
            }) 
            .then((data) => { 
                setAllSongs(data); 
                setIsLoading(false);
            }) 
            .catch((err) => {
                console.error("Error fetching songs:", err);
                setIsLoading(false);
            }); 
    }, []); 

    // Prepare categories, filtering by search query first, then by song data
    const categoriesWithCovers = useMemo(() => {
        // 1. Filter the static config list based on search query
        const lowerCaseQuery = searchQuery.toLowerCase();
        const searchableCategories = CATEGORIES_CONFIG.filter(category => 
            category.title.toLowerCase().includes(lowerCaseQuery) ||
            category.id.includes(lowerCaseQuery) 
        );

        if (allSongs.length === 0) return searchableCategories.map(category => ({
            ...category,
            covers: [],
            filteredSongs: [],
            songCount: 0
        }));


        // 2. Then, map and filter songs for the covers and detail data
        return searchableCategories.map(category => {
            const filteredSongs = allSongs.filter(category.filter);
            const covers = filteredSongs
                .slice(0, 4)
                .map(song => song.cover)
                .filter(cover => cover); 

            return {
                ...category,
                covers: covers,
                filteredSongs: filteredSongs, 
                songCount: filteredSongs.length
            };
        });
    }, [allSongs, searchQuery]); 

    // Handler for clicking a category card (UNCHANGED)
    const handleCategoryClick = (category) => {
        navigate(`/categories/${category.id}`, { 
            state: { 
                title: category.title,
                songs: category.filteredSongs 
            } 
        });
    };
    
    // Determine content based on state
    let content;

    // Use a unified loading message style
    const Message = ({ text }) => <p className="loading-message" style={{color: '#999', textAlign: 'center', marginTop: '50px'}}>{text}</p>;

    if (isLoading) {
        content = <Message text="Loading categories..." />;
    } else if (categoriesWithCovers.length === 0 && searchQuery) {
        content = <Message text={`No categories found matching "${searchQuery}".`} />;
    } else if (categoriesWithCovers.length === 0) {
        content = <Message text="No categories found or defined." />;
    } else {
        // Show the actual grid
        content = (
            <div className="category-grid">
                {categoriesWithCovers.map((category) => (
                    <CategoryCard 
                        key={category.id} 
                        category={category}
                        covers={category.covers}
                        onSelect={handleCategoryClick}
                    />
                ))}
            </div>
        );
    }

    return (
        <div className="categories-page">
            <h2 className="categories-title">Browse All Categories & Playlists</h2>
            
            {/* Search Bar */}
            <div className="search-bar-container categories-search">
                <input
                    type="text"
                    placeholder="Search categories and playlists..."
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    className="search-input"
                    aria-label="Search categories"
                />
            </div>
            
            {content}
        </div>
    );
}
