             { "id": 65, "moviename": "Thalaivan Thalaivii", "songname": "Pottala Muttaye", "artist": "Gana Balachandar, Santhosh Narayanan", "language": "Tamil", "songvariety": [ "romantic", "trending", "dance", "folk" ], "characters": "Ashok Selvan, Keerthi Pandian", "url": "/music/Pottala Muttaye.mp3", "duration": "3:58", "cover": "/covers/PottalaMuttaye.jpeg" }
               ];

        setAllSongs(data); 
        setIsLoading(false);
    }, []); 

    // Navigation handler
    const handleCategorySelect = (category) => {
        // If it's a dynamic playlist, we navigate using its ID
        if (category.isDynamic) {
            console.log(`Navigating to dynamic playlist: ${category.title}`);
            navigate(`/playlist/${category.id}`);
        } else {
            // If it's a static category, navigate as before
            console.log(`Navigating to static category: ${category.title}`);
            navigate(`/playlist/${category.id}`);
        }
    };

    // Prepare categories, filtering by search query and adding cover data
    const groupedCategoriesWithCovers = useMemo(() => {
        const lowerCaseQuery = searchQuery.toLowerCase();
        const allCategoriesToSearch = [...ALL_STATIC_CATEGORIES, ...playlists];

        // Function to process a flat list of categories
        const processCategories = (categories, isDynamic = false) => {
            // Only filter by title/id for the search results (or if not dynamic)
            const searchableCategories = categories.filter(category => 
                category.title.toLowerCase().includes(lowerCaseQuery) ||
                category.id.includes(lowerCaseQuery) ||
                // Dynamic playlists should remain in search even if they don't have covers yet
                isDynamic 
            );

            // 2. Map and filter songs for the covers and detail data
            return searchableCategories.map(category => {
                // If it's a dynamic playlist from context, use its `songs` array for filtering
                const songsToFilter = isDynamic ? category.songs : allSongs.filter(category.filter);
                
                const filteredSongs = songsToFilter;
                
                const covers = filteredSongs
                    .slice(0, 4)
                    .map(song => song.cover)
                    .filter(cover => cover); 

                // Fill up to 4 covers with DEFAULT_COVER
                while (covers.length < 4) {
                    covers.push(DEFAULT_COVER);
                }

                return {
                    ...category,
                    isDynamic, // Mark it as dynamic
                    covers,
                    filteredSongs,
                    songCount: filteredSongs.length
                };
            });
        };

        // --- Grouping Logic ---
        
        // 1. Handle Search Query (Flatten everything)
        if (searchQuery) {
            // Determine if the category is dynamic for cover calculation
            const searchResults = allCategoriesToSearch.map(category => {
                const isDynamic = !!playlists.find(p => p.id === category.id);
                return processCategories([category], isDynamic)[0];
            }).filter(category => 
                category && 
                (category.title.toLowerCase().includes(lowerCaseQuery) || category.id.includes(lowerCaseQuery))
            );
            
            if (searchResults.length > 0) {
                 return { "Search Results": searchResults };
            } else {
                return {};
            }
        }

        // 2. Handle No Search Query (Grouped View)
        const result = {};

        // A. Add Dynamic Playlists First
        const dynamicPlaylistsProcessed = processCategories(playlists, true);
        if (dynamicPlaylistsProcessed.length > 0) {
            result["Your Playlists"] = dynamicPlaylistsProcessed;
        }

        // B. Add Static Categories
        for (const [groupName, categories] of Object.entries(STATIC_CATEGORY_GROUPS)) {
            const processed = processCategories(categories);
            if (processed.length > 0) {
                result[groupName] = processed;
            }
        }
        
        return result;

    }, [searchQuery, allSongs, playlists]); // Dependency on playlists ensures updates after song is added

    if (isLoading) {
        return <div className="loading">Loading Categories... 🎶</div>;
    }

    const allProcessedCategories = Object.values(groupedCategoriesWithCovers).flat();

    return (
        <div className="categories-page">
            <h1 className="page-title">Explore Playlists & Artists</h1>
            <div className="search-container">
                <input
                    type="text"
                    placeholder="Search Playlists or Artists (e.g., Peppy, My Favourites)"
                    className="search-input"
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    aria-label="Search categories"
                />
            </div>
            
            {allProcessedCategories.length === 0 && searchQuery && (
                <p className="no-results">No playlists match your search: "{searchQuery}"</p>
            )}

            {Object.entries(groupedCategoriesWithCovers).map(([groupName, categories]) => (
                // Only render the group if it contains categories
                categories.length > 0 && (
                    <React.Fragment key={groupName}>
                        <h2 style={{ color: '#fff', fontSize: '1.5rem', marginTop: '30px', marginBottom: '15px' }}>{groupName}</h2>
                        <div className="categories-grid">
                            {categories.map((category) => (
                                <CategoryCard
                                    key={category.isDynamic ? `dynamic-${category.id}` : category.id}
                                    category={category}
                                    covers={category.covers}
                                    onSelect={handleCategorySelect}
                                />
                            ))}
                        </div>
                    </React.Fragment>
                )
            ))}
        </div>
    );
}
