// src/deezerService.js
import axios from "axios";

const ALLORIGINS = "https://api.allorigins.win/raw?url=";
const DEEZER_API = "https://api.deezer.com/search?q=";

export async function searchDeezer(query) {
  try {
    const url = `${ALLORIGINS}${encodeURIComponent(DEEZER_API + query)}`;
    const response = await axios.get(url);

    return response.data.data.map((song) => ({
      id: song.id,
      title: song.title,
      artist: song.artist.name,
      language: "Unknown",
      duration: `${Math.floor(song.duration / 60)}:${String(song.duration % 60).padStart(2, "0")}`,
      cover: song.album.cover_medium,
      url: song.preview,
    }));
  } catch (error) {
    console.error("Error fetching from Deezer:", error);
    return [];
  }
}
