import React from "react";
import { useAuth } from "../context/AuthContext";
import "./Greeting.css"; // optional: for styling popup & text

export default function Greeting() {
  const { user, showAuthPopup, authPopupMessage, triggerAuthPopup } = useAuth();

  // Example: handler when trying to access restricted features
  const handleRestrictedAction = () => {
    if (!user) {
      triggerAuthPopup("You need to log in to access this feature!");
    }
  };

  return (
    <div className="greeting-container">
      <h2>
        <span className="name-color">
          {user ? `${user.username}'s` : "My"}
        </span>{" "}
        Playlist
      </h2>

      {/* Example button to test restricted feature */}
      <button onClick={handleRestrictedAction} className="restricted-btn">
        Open Playlist
      </button>

      {/* Pop-up notification */}
      {showAuthPopup && (
        <div className="auth-popup">
          {authPopupMessage}
        </div>
      )}
    </div>
  );
}
