import React, { useState, useEffect, useMemo } from "react";
import { useNavigate } from "react-router-dom"; 
import "./Categories.css"; 

// A default cover image for categories that might not have enough songs
const DEFAULT_COVER = "https://placehold.co/100x100/303030/ffffff?text=Music";

// --- Configuration for all Category/Playlist Cards ---
// The 'filter' property defines how songs are selected for this category.
const CATEGORIES_CONFIG = [
    // Languages
    { id: "tamil", title: "Top Tamil Songs", filter: (song) => song.language?.toLowerCase().includes('tamil') },
    { id: "english", title: "International English Hits", filter: (song) => song.language?.toLowerCase().includes('english') },
    { id: "hindi", title: "Bollywood Hindi Hits", filter: (song) => song.language?.toLowerCase().includes('hindi') },
    
    // Playlists/Moods/Genres (Using songvariety array)
    { id: "trending", title: "ðŸ”¥ Trending Now Package", filter: (song) => song.songvariety?.includes('trending') },
    { id: "mass", title: "Mass/Kuthu Hits", filter: (song) => song.songvariety?.includes('mass') },
    { id: "love", title: "ðŸ’– Pure Love Songs", filter: (song) => song.songvariety?.includes('romantic') || song.songvariety?.includes('love') },
    { id: "sad", title: "ðŸ¥º Sad/Melody", filter: (song) => song.songvariety?.includes('sad') || song.songvariety?.includes('melody') },
    { id: "vibe", title: "âœ¨ Vibe/Chill Songs", filter: (song) => song.songvariety?.includes('vibe') || song.songvariety?.includes('chill') },
    { id: "party", title: "ðŸŽ‰ Party Starters", filter: (song) => song.songvariety?.includes('party') },
    { id: "dance", title: "ðŸ’ƒ Dance Floor Hits", filter: (song) => song.songvariety?.includes('dance') },

    // Artist Playlists (Assuming artist names are comma-separated in the JSON)
    { id: "anirudh", title: "Anirudh Ravichander Playlist", filter: (song) => song.artist?.includes('Anirudh Ravichander') },
    { id: "sidsriram", title: "Sid Sriram Collection", filter: (song) => song.artist?.includes('Sid Sriram') },
];

// --- Category Card Component (UNCHANGED) ---
const CategoryCard = React.memo(({ category, covers, onSelect }) => {
    return (
        <div 
            className="category-card" 
            onClick={() => onSelect(category)}
            aria-label={`View songs in ${category.title}`}
        >
            <h3>{category.title}</h3>
            <div className="category-images">
                {/* Always render 4 images, using DEFAULT_COVER for placeholders */}
                {Array.from({ length: 4 }).map((_, i) => (
                    <img 
                        key={i} 
                        src={covers[i] || DEFAULT_COVER} 
                        alt={`${category.title} album cover ${i + 1}`} 
                        onError={(e) => { e.target.onerror = null; e.target.src = DEFAULT_COVER; }}
                    />
                ))}
            </div>
        </div>
    );
});


export default function Categories() {
    const [allSongs, setAllSongs] = useState([]);
    const navigate = useNavigate(); // Hook for navigation

    // Fetch all songs once
    useEffect(() => { 
        fetch("/songs.json") 
          .then((res) => res.json()) 
          .then((data) => { 
            setAllSongs(data); 
          }) 
          .catch((err) => console.error("Error fetching songs:", err)); 
    }, []); 

    // Prepare categories, filtering songs and getting covers
    const categoriesWithCovers = useMemo(() => {
        if (allSongs.length === 0) return [];

        return CATEGORIES_CONFIG.map(category => {
            // Filter songs based on the defined rule
            const filteredSongs = allSongs.filter(category.filter);

            // Extract the covers of the first 4 songs
            const covers = filteredSongs
                .slice(0, 4)
                .map(song => song.cover)
                .filter(cover => cover); 

            return {
                ...category,
                covers: covers,
                filteredSongs: filteredSongs, // Store the full list of filtered songs
                songCount: filteredSongs.length
            };
        });
    }, [allSongs]);

    // Handler for clicking a category card (MODIFIED)
    const handleCategoryClick = (category) => {
        // Navigate to a new route, passing the category title and song list via state
        navigate(`/categories/${category.id}`, { 
            state: { 
                title: category.title,
                songs: category.filteredSongs 
            } 
        });
    };

    return (
        <div className="categories-page">
            <h2 className="categories-title">Browse All Categories & Playlists</h2>
            
            <div className="category-grid">
                {categoriesWithCovers.map((category) => (
                    <CategoryCard 
                        key={category.id} 
                        category={category}
                        covers={category.covers}
                        onSelect={handleCategoryClick}
                    />
                ))}
                {categoriesWithCovers.length === 0 && allSongs.length > 0 && (
                    <p style={{color: '#999', gridColumn: '1 / -1'}}>No categories found or defined.</p>
                )}
                {allSongs.length === 0 && (
                     <p style={{color: '#999', gridColumn: '1 / -1'}}>Loading categories...</p>
                )}
            </div>
        </div>
    );
}
