import React, { useState, useEffect, useMemo } from "react";
import { useMusicPlayer } from '../context/MusicPlayerContext';
import "./Home.css"; 
import "../components/Search.css"; // Reusing Song Card styles

const SONGS_PER_ROW = 6;
const HISTORY_KEY = 'playedSongHistory'; // Key used in MusicPlayerContext for history

// --- LOCAL STORAGE UTILITY ---
// Gets the list of song IDs that have been played recently
const getPlayedHistory = () => {
    try {
        const history = localStorage.getItem(HISTORY_KEY);
        // Returns a Set to ensure unique IDs, then converts to Array
        return history ? Array.from(new Set(JSON.parse(history))) : [];
    } catch (e) {
        return [];
    }
};

// --- SongRow Component (with Hover Class) ---
const SongRow = React.memo(({ title, songs, activeSong, isPlaying, playSong, categoryId }) => {
    if (!songs || songs.length === 0) return null;
    
    // Duplicate the songs array to create a seamless looping effect in CSS
    const infiniteSongs = [...songs, ...songs];

    return (
        <section className="song-section">
            <h2 className="section-title" id={`title-${categoryId}`}>{title}</h2>
            
            {/* The hover class 'hover-scroll' is applied to the section container */}
            <div className={`song-row-container ${categoryId}-scroll-area`}>
                {/* Render the duplicated list for infinite train effect */}
                <div className="song-row-content">
                    {infiniteSongs.map((song, index) => (
                        <div 
                            key={`${song.id}-${index}`} // Use index in key since songs are duplicated
                            className={`song-card ${activeSong?.id === song.id && isPlaying ? 'is-playing' : ''}`}
                            onClick={() => playSong(song)}
                        >
                            <img 
                                src={song.cover} 
                                alt={song.songname} 
                                className="cover" 
                                onError={(e) => { e.target.onerror = null; e.target.src="https://placehold.co/200x200/404040/ffffff?text=No+Cover"; }}
                            />
                            <h4>{song.songname}</h4>
                            <p>{song.artist.split(',')[0]}</p>
                            {song.movieName && <p className="song-detail">Movie: {song.movieName}</p>}
                        </div>
                    ))}
                </div>
            </div>
        </section>
    );
});


export default function Home() {
    const [allSongs, setAllSongs] = useState([]);
    const { activeSong, isPlaying, playSong } = useMusicPlayer();

    // Fetch All Songs
    useEffect(() => {
        fetch("/songs.json")
          .then((res) => res.json())
          .then((data) => {
            setAllSongs(data);
          })
          .catch((err) => console.error("Error fetching songs:", err));
    }, []);


    // 1. Recently Played Songs (Based on Local Storage History)
    const recentlyPlayed = useMemo(() => {
        const historyIds = getPlayedHistory();
        
        // Find the full song objects that match the IDs, maintaining order
        const songs = historyIds.map(id => 
            allSongs.find(s => s.id === id)
        ).filter(s => s !== undefined); // Remove any songs not found

        // The history is already ordered from most recent, so we just slice it.
        return songs.slice(0, SONGS_PER_ROW); 
    }, [allSongs]);

    // 2. Trending Songs (Fetch based on songVerity field)
    const trendingSongs = useMemo(() => {
        // ASSUMPTION: 'songVerity' exists in your JSON and is a number (higher is better)
        const sorted = [...allSongs].sort((a, b) => (b.songVerity || 0) - (a.songVerity || 0));
        return sorted.slice(0, SONGS_PER_ROW);
    }, [allSongs]);

    // 3. Tamil Songs
    const tamilSongs = useMemo(() => {
        return allSongs.filter(song => 
            song.language && song.language.toLowerCase().includes('tamil')
        ).slice(0, SONGS_PER_ROW);
    }, [allSongs]);

    // 4. English Songs
    const englishSongs = useMemo(() => {
        return allSongs.filter(song => 
            song.language && song.language.toLowerCase().includes('english')
        ).slice(0, SONGS_PER_ROW);
    }, [allSongs]);
    
    // 5. Hindi Songs (NEW SECTION)
    const hindiSongs = useMemo(() => {
        return allSongs.filter(song => 
            song.language && song.language.toLowerCase().includes('hindi')
        ).slice(0, SONGS_PER_ROW);
    }, [allSongs]);

    const isLoading = allSongs.length === 0;

    return (
        <div className="home-dashboard">
            <h1 className="main-welcome">ðŸŽµ Welcome to My Music Hub</h1>
            
            {isLoading ? (
                <p className="loading-message">Loading your music library...</p>
            ) : (
                <>
                    {/* Row 1: Recently Played */}
                    <SongRow
                        title="Your Recently Played"
                        songs={recentlyPlayed}
                        categoryId="recent"
                        activeSong={activeSong}
                        isPlaying={isPlaying}
                        playSong={playSong}
                    />

                    {/* Row 2: Global Trending Hits (using songVerity) */}
                    <SongRow
                        title="Global Trending Hits"
                        songs={trendingSongs}
                        categoryId="trending"
                        activeSong={activeSong}
                        isPlaying={isPlaying}
                        playSong={playSong}
                    />
                    
                    {/* Row 3: Top Tamil Melodies */}
                    <SongRow
                        title="Top Tamil Melodies"
                        songs={tamilSongs}
                        categoryId="tamil"
                        activeSong={activeSong}
                        isPlaying={isPlaying}
                        playSong={playSong}
                    />

                    {/* Row 4: International English Favorites */}
                    <SongRow
                        title="International English Favorites"
                        songs={englishSongs}
                        categoryId="english"
                        activeSong={activeSong}
                        isPlaying={isPlaying}
                        playSong={playSong}
                    />
                    
                    {/* Row 5: Best of Hindi Cinema */}
                    <SongRow
                        title="Best of Hindi Cinema"
                        songs={hindiSongs}
                        categoryId="hindi"
                        activeSong={activeSong}
                        isPlaying={isPlaying}
                        playSong={playSong}
                    />
                </>
            )}
        </div>
    );
}
