import axios from "axios";

// Use corsproxy.io instead of AllOrigins (more reliable)
const CORS_PROXY = "https://corsproxy.io/?";
const DEEZER_API = "https://api.deezer.com/search?q=";

export async function searchDeezer(query, limit = 30, language = "Unknown") {
  try {
    const url = `${CORS_PROXY}${encodeURIComponent(
      `${DEEZER_API}${query}&limit=${limit}`
    )}`;

    const response = await axios.get(url);

    return response.data.data.map((song) => ({
      id: song.id,
      title: song.title,
      artist: song.artist.name,
      language, // manually assign based on query
      duration: `${Math.floor(song.duration / 60)}:${String(
        song.duration % 60
      ).padStart(2, "0")}`,
      cover: song.album.cover_medium,
      url: song.preview,
    }));
  } catch (error) {
    console.error("Error fetching from Deezer:", error);
    return [];
  }
}
