import React, { useState, useEffect, useMemo } from "react";
import { useNavigate } from "react-router-dom";
import "./Categories.css";
import "../components/Search.css";

const DEFAULT_COVER = "https://placehold.co/100x100/303030/ffffff?text=Music";

const CATEGORIES_CONFIG = [
  { id: "tamil", title: "ðŸ‡®ðŸ‡³ Top Tamil Songs", filter: (song) => song.language?.toLowerCase().includes("tamil") },
  { id: "english", title: "ðŸŒ International English Hits", filter: (song) => song.language?.toLowerCase().includes("english") },
  { id: "hindi", title: "ðŸŽ¼ Bollywood Hindi Hits", filter: (song) => song.language?.toLowerCase().includes("hindi") },
  { id: "trending", title: "ðŸ”¥ Trending Now Package", filter: (song) => song.songvariety?.includes("trending") },
  { id: "mass", title: "ðŸ’¥ Mass/Kuthu Hits", filter: (song) => song.songvariety?.includes("mass") },
  { id: "love", title: "ðŸ’– Pure Love Songs", filter: (song) => song.songvariety?.includes("romantic") || song.songvariety?.includes("love") },
  { id: "sad", title: "ðŸ˜¢ Sad/Melody", filter: (song) => song.songvariety?.includes("sad") || song.songvariety?.includes("melancholy") },
  { id: "feel-good", title: "ðŸ˜Š Feel Good Hits", filter: (song) => song.songvariety?.includes("feel good") },
  { id: "vibe", title: "ðŸ§˜ Vibe/Chill Songs", filter: (song) => song.songvariety?.includes("vibe") },
  { id: "party", title: "ðŸ¥³ Party Starters", filter: (song) => song.songvariety?.includes("party") },
  { id: "dance", title: "ðŸ’ƒ Dance Floor Hits", filter: (song) => song.songvariety?.includes("dance") },
  { id: "favorites", title: "â­ My Favorites", filter: () => false } // special category
];

// --- Category Card Component ---
const CategoryCard = React.memo(({ category, covers, onSelect }) => {
  return (
    <div
      className="category-card"
      onClick={() => onSelect(category)}
      aria-label={`View songs in ${category.title}`}
    >
      <div className="category-images">
        {Array.from({ length: 4 }).map((_, i) => (
          <img
            key={i}
            src={covers[i] || DEFAULT_COVER}
            alt={`${category.title} album cover ${i + 1}`}
            onError={(e) => {
              e.target.onerror = null;
              e.target.src = DEFAULT_COVER;
            }}
          />
        ))}
      </div>
      <h3>{category.title}</h3>
    </div>
  );
});

// --- Song Card with + Button ---
const SongCard = ({ song, onAddFavorite }) => {
  return (
    <div className="song-card">
      <img src={song.cover || DEFAULT_COVER} alt={song.songname} />
      <div className="song-info">
        <p>{song.songname}</p>
        <small>{song.artist}</small>
      </div>
      <button className="add-btn" onClick={() => onAddFavorite(song)}>+</button>
    </div>
  );
};

export default function Categories() {
  const [allSongs, setAllSongs] = useState([]);
  const [favorites, setFavorites] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [isLoading, setIsLoading] = useState(true);
  const navigate = useNavigate();

  // Load songs
  useEffect(() => {
    const data = [
      { "id": 1, "moviename": "Theeran", "songname": "Laali Laali", "artist": "Sathyaprakash D., Priya Hemesh", "language": "Tamil", "songvariety": ["romantic", "love"], "url": "/music/Laali Laali.mp3", "cover": "/covers/Laali Laali.jpg" },
      { "id": 2, "moviename": "Aadhavan", "songname": "Vaarayo Vaarayao", "artist": "M. M. Keeravani, K. S. Chithra, S. P. Balasubrahmanyam", "language": "Tamil", "songvariety": ["love", "party", "dance", "romantic"], "url": "/music/Vaarayo Vaarayo.mp3", "cover": "/covers/Vaarayao Vaarayao.jpg" },
      // ... continue all songs you pasted ...
    ];
    setAllSongs(data);
    setIsLoading(false);
  }, []);

  const handleAddFavorite = (song) => {
    setFavorites((prev) => {
      if (!prev.find((s) => s.id === song.id)) {
        return [...prev, song];
      }
      return prev;
    });
  };

  // Compute covers for categories
  const categoryCovers = useMemo(() => {
    return CATEGORIES_CONFIG.map((category) => {
      if (category.id === "favorites") return favorites.map((s) => s.cover);
      return allSongs.filter(category.filter).slice(0, 4).map((s) => s.cover);
    });
  }, [allSongs, favorites]);

  const handleSelectCategory = (category) => {
    if (category.id === "favorites") {
      navigate("/favorites", { state: { songs: favorites } });
    } else {
      const songs = allSongs.filter(category.filter);
      navigate(`/category/${category.id}`, { state: { songs } });
    }
  };

  if (isLoading) return <p>Loading songs...</p>;

  return (
    <div className="categories-page">
      <input
        type="text"
        className="search-bar"
        placeholder="Search songs..."
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
      />

      <div className="song-list">
        {allSongs
          .filter((song) => song.songname.toLowerCase().includes(searchQuery.toLowerCase()))
          .map((song) => (
            <SongCard key={song.id} song={song} onAddFavorite={handleAddFavorite} />
          ))}
      </div>

      <h2>Categories</h2>
      <div className="categories-grid">
        {CATEGORIES_CONFIG.map((category, i) => (
          <CategoryCard
            key={category.id}
            category={category}
            covers={categoryCovers[i]}
            onSelect={handleSelectCategory}
          />
        ))}
      </div>
    </div>
  );
}
