// src/pages/Dashboard.jsx
import { useEffect, useState } from "react";
import SongCard from "../components/SongCard";

export default function Dashboard() {
  const [tamilSongs, setTamilSongs] = useState([]);
  const [englishSongs, setEnglishSongs] = useState([]);

  useEffect(() => {
    // Fetch songs from public/songs.json
    fetch("/songs.json")
      .then((res) => res.json())
      .then((data) => {
        setTamilSongs(data.filter((song) => song.language === "Tamil"));
        setEnglishSongs(data.filter((song) => song.language === "English"));
      })
      .catch((err) => console.error("Error loading songs.json", err));
  }, []);

  return (
    <div>
      <h2>ðŸŽ¶ Tamil Songs</h2>
      <div style={{ display: "flex", flexWrap: "wrap" }}>
        {tamilSongs.map((song) => (
          <SongCard key={song.id} song={song} />
        ))}
      </div>

      <h2>ðŸŽµ English Songs</h2>
      <div style={{ display: "flex", flexWrap: "wrap" }}>
        {englishSongs.map((song) => (
          <SongCard key={song.id} song={song} />
        ))}
      </div>
    </div>
  );
}
