import React, { createContext, useContext, useState, useRef, useEffect, useCallback } from 'react';
// Assuming your player styles are in SongCard.css and accessible globally
import '../components/SongCard.css'; 

// 1. Create Context
const MusicPlayerContext = createContext();

// 2. Custom Hook for easy consumption
export const useMusicPlayer = () => {
    return useContext(MusicPlayerContext);
};

// Helper function to format seconds (Moved from SongCard.jsx)
const formatTime = (seconds) => {
    if (isNaN(seconds) || seconds < 0) return "0:00";
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = Math.floor(seconds % 60);
    return `${minutes}:${remainingSeconds.toString().padStart(2, '0')}`;
};

// Mock data structure for initial playlists
const initialPlaylists = [
    { id: 'p1', name: 'My Favourites', songs: [] },
    { id: 'p2', name: 'Road Trip Jams', songs: [] },
];

// 3. Provider Component (Holds all state and logic)
export const MusicPlayerProvider = ({ children }) => {
    // --- State (Global to the entire app) ---
    const [songs, setSongs] = useState([]); 
    const [activeSong, setActiveSong] = useState(null);
    const [isPlaying, setIsPlaying] = useState(false);
    const [currentTime, setCurrentTime] = useState(0);
    const [duration, setDuration] = useState(0);
    const audioRef = useRef(null);
    
    // --- NEW: Playlist State ---
    const [playlists, setPlaylists] = useState(initialPlaylists);
    // const [currentPlaylist, setCurrentPlaylist] = useState(null); // Useful for playlist playback

    // --- Core Playback Functions (Existing) ---

    const skipTrack = useCallback((direction) => {
        if (!activeSong || songs.length === 0) return;
        
        const currentIndex = songs.findIndex(s => s.id === activeSong.id);
        let newIndex = currentIndex + direction;
        
        if (newIndex < 0) {
            newIndex = songs.length - 1; // Loop to last song
        } else if (newIndex >= songs.length) {
            newIndex = 0; // Loop to first song
        }
        
        const nextSong = songs[newIndex];
        if (nextSong) {
            setActiveSong(nextSong);
            setIsPlaying(true);
        }
    }, [activeSong, songs]); 

    const handleLoadedMetadata = useCallback(() => {
        if (audioRef.current) {
            setDuration(audioRef.current.duration);
        }
    }, []);

    const handleTimeUpdate = useCallback(() => {
        if (audioRef.current) {
            setCurrentTime(audioRef.current.currentTime);
        }
    }, []);

    const handleSongEnded = useCallback(() => {
        skipTrack(1); 
    }, [skipTrack]); 

    const togglePlayPause = () => {
        const audio = audioRef.current;
        if (!audio) return;

        if (isPlaying) {
            audio.pause();
        } else {
            audio.play().catch(e => console.error("Play failed:", e));
        }
        setIsPlaying(!isPlaying);
    };

    const handleSeek = (e) => {
        const audio = audioRef.current;
        if (audio) {
            const newTime = parseFloat(e.target.value);
            audio.currentTime = newTime;
            setCurrentTime(newTime);
        }
    };
    
    // Function exposed to SongCard.jsx to start/toggle playback
    const playSong = (song) => {
        if (activeSong?.id !== song.id) {
            setActiveSong(song);
            setIsPlaying(true);
        } else {
            togglePlayPause();
        }
    };

    // --- NEW: Playlist Management Functions ---
    
    const addToPlaylist = useCallback((playlistId, song) => {
        setPlaylists(prevPlaylists =>
            prevPlaylists.map(playlist =>
                playlist.id === playlistId && !playlist.songs.some(s => s.id === song.id)
                    ? { ...playlist, songs: [...playlist.songs, song] }
                    : playlist
            )
        );
        console.log(`Added ${song.songname} to playlist ID: ${playlistId}`);
    }, []);

    const createPlaylist = useCallback((name) => {
        const newPlaylist = {
            id: Date.now().toString(), // Simple unique ID
            name: name || `New Playlist ${playlists.length + 1}`,
            songs: activeSong ? [activeSong] : [] // Optionally add the active song
        };
        setPlaylists(prevPlaylists => [...prevPlaylists, newPlaylist]);
        console.log(`Created new playlist: ${newPlaylist.name}`);
        return newPlaylist;
    }, [playlists.length, activeSong]);

    // --- Effects (Existing) ---
    
    // 1. Attach/Detach event listeners
    useEffect(() => {
        const audio = audioRef.current;
        if (!audio) return;

        audio.addEventListener('loadedmetadata', handleLoadedMetadata);
        audio.addEventListener('timeupdate', handleTimeUpdate);
        audio.addEventListener('ended', handleSongEnded);

        return () => {
            audio.removeEventListener('loadedmetadata', handleLoadedMetadata);
            audio.removeEventListener('timeupdate', handleTimeUpdate);
            audio.removeEventListener('ended', handleSongEnded);
        };
    }, [handleLoadedMetadata, handleTimeUpdate, handleSongEnded, activeSong]);

    // 2. Handles auto-play after song load/change
    useEffect(() => {
        if (activeSong && audioRef.current && isPlaying) {
            audioRef.current.play().catch(e => {
                console.warn("Autoplay prevented. User needs to interact.");
                setIsPlaying(false); 
            });
        }
    }, [activeSong, isPlaying]);


    const contextValue = {
        activeSong,
        isPlaying,
        currentTime,
        duration,
        songs,
        playSong,        
        togglePlayPause,
        skipTrack,
        handleSeek,
        setSongs,
        // --- NEW: Playlist Context Values ---
        playlists,
        addToPlaylist,
        createPlaylist,
    };

    // Placeholder function for UI trigger
    const handlePlaylistIconHover = () => {
        // In a real application, this would toggle a state for a modal or dropdown
        // to show playlists.
        if (playlists.length === 0) {
            // Logic to prompt the user to create one
            console.log("No playlists found. Prompting user to create a new one.");
        } else {
            // Logic to show the list of playlists
            console.log("Showing list of existing playlists.");
        }
    };

    return (
        <MusicPlayerContext.Provider value={contextValue}>
            {children}
            
            {/* --- 4. The Persistent Player JSX --- */}
            {activeSong && (
                <div className="player-footer">
                    <audio 
                        ref={audioRef}
                        key={activeSong.id} 
                        src={activeSong.url}
                        preload="auto"
                    />
                    
                    {/* Song Info (Cover + Text Block) */}
                    <div className="song-info player-info">
                        <img 
                            src={activeSong.cover} 
                            alt={activeSong.songname} 
                            className="player-cover" 
                        />
                        <div>
                            <h4>{activeSong.songname}</h4>
                            <p>{activeSong.artist.split(',')[0]}</p>
                        </div>
                    </div>

                    {/* Controls and Progress */}
                    <div className="progress-container">
                        <div className="player-controls">
                            <button onClick={() => skipTrack(-1)}>&lt;&lt;</button>
                            <button onClick={togglePlayPause}>
                                {isPlaying ? '❚❚' : '▶'}
                            </button>
                            <button onClick={() => skipTrack(1)}>&gt;&gt;</button>
                        </div>

                        {/* Time and Seek Bar */}
                        <span className="progress-time">{formatTime(currentTime)}</span>
                        <input
                            type="range"
                            min="0"
                            max={duration || 0}
                            value={currentTime}
                            className="progress-bar"
                            onChange={handleSeek}
                        />
                        <span className="progress-time">{formatTime(duration)}</span>
                    </div>

                    {/* Volume and Other Options (Placeholder) */}
                    <div className="player-options-right" style={{ flex: 1, textAlign: 'right', position: 'relative' }}>
                        
                        {/* NEW: Add to Playlist Button */}
                        <button 
                            className="add-to-playlist-btn"
                            onMouseEnter={handlePlaylistIconHover}
                            // You'd typically use a state variable for this dropdown visibility
                            // onMouseLeave={() => setShowPlaylistDropdown(false)} 
                            style={{ 
                                background: 'none', 
                                border: 'none', 
                                color: 'white', 
                                fontSize: '24px', 
                                cursor: 'pointer',
                                padding: '0 10px'
                            }}
                        >
                            +
                        </button>
                    </div>
                </div>
            )}
        </MusicPlayerContext.Provider>
    );
};
