import React, { createContext, useContext, useState, useRef, useEffect, useCallback } from 'react';

const MusicPlayerContext = createContext();

export const useMusicPlayer = () => useContext(MusicPlayerContext);

const initialPlaylists = [
  { id: 'p1', name: 'My Favourites', songs: [] },
  { id: 'p2', name: 'Road Trip Jams', songs: [] },
];

export const MusicPlayerProvider = ({ children }) => {
  const [songs, setSongs] = useState([]); // optional global library
  const [activeSong, setActiveSong] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentTime, setCurrentTime] = useState(0);
  const [duration, setDuration] = useState(0);
  const audioRef = useRef(null);

  const [playlists, setPlaylists] = useState(initialPlaylists);

  // --- Playback controls used internally ---
  const playNext = useCallback(() => {
    if (!songs || songs.length === 0) return;
    if (!activeSong) {
      setActiveSong(songs[0]);
      setIsPlaying(true);
      return;
    }
    const idx = songs.findIndex(s => s.id === activeSong.id);
    const nextIndex = (idx + 1 + songs.length) % songs.length;
    setActiveSong(songs[nextIndex]);
    setIsPlaying(true);
  }, [activeSong, songs]);

  const playPrevious = useCallback(() => {
    if (!songs || songs.length === 0) return;
    if (!activeSong) {
      setActiveSong(songs[0]);
      setIsPlaying(true);
      return;
    }
    const idx = songs.findIndex(s => s.id === activeSong.id);
    const prevIndex = (idx - 1 + songs.length) % songs.length;
    setActiveSong(songs[prevIndex]);
    setIsPlaying(true);
  }, [activeSong, songs]);

  // toggle play/pause (will attempt to play or pause the hidden audio element)
  const togglePlayPause = useCallback(() => {
    const audio = audioRef.current;
    if (!audio) {
      // If there's no audio element yet, just flip state
      setIsPlaying(prev => !prev);
      return;
    }

    if (isPlaying) {
      audio.pause();
      setIsPlaying(false);
    } else {
      const promise = audio.play();
      if (promise !== undefined) {
        promise
          .then(() => setIsPlaying(true))
          .catch((err) => {
            console.warn("Playback prevented/failed:", err);
            setIsPlaying(false);
          });
      } else {
        // older browsers
        setIsPlaying(true);
      }
    }
  }, [isPlaying]);

  // Play a specific song (used by song cards)
  const playSong = useCallback((song) => {
    if (!song) return;
    if (!activeSong || activeSong.id !== song.id) {
      setActiveSong(song);
      // set isPlaying true -> effect will call audio.play()
      setIsPlaying(true);
    } else {
      // same song -> toggle
      togglePlayPause();
    }
  }, [activeSong, togglePlayPause]);

  // Seek handler accepts either an event or a numeric value
  const handleSeek = useCallback((valueOrEvent) => {
    const audio = audioRef.current;
    const value = typeof valueOrEvent === 'object' ? parseFloat(valueOrEvent.target.value) : parseFloat(valueOrEvent);
    if (!isNaN(value) && audio) {
      audio.currentTime = value;
      setCurrentTime(value);
    }
  }, []);

  // Add to existing playlist (by id)
  const addToPlaylist = useCallback((playlistId, song) => {
    if (!song) return;
    setPlaylists(prev =>
      prev.map(pl => pl.id === playlistId
        ? (pl.songs.some(s => s.id === song.id) ? pl : { ...pl, songs: [...pl.songs, song] })
        : pl
      )
    );
  }, []);

  // Create a playlist (returns the new playlist object)
  const createPlaylist = useCallback((name, initialSong = null) => {
    const newPlaylist = {
      id: Date.now().toString(),
      name: name || `New Playlist`,
      songs: initialSong ? [initialSong] : []
    };
    setPlaylists(prev => [...prev, newPlaylist]);
    return newPlaylist;
  }, []);

  // Button handler used by your "+" UI
  const handleAddToPlaylist = useCallback(() => {
    if (!activeSong) return;
    // find My Favourites (case-sensitive match on name used earlier)
    let favourites = playlists.find(p => p.name === "My Favourites");

    if (!favourites) {
      // create and add active song
      const created = createPlaylist("My Favourites", activeSong);
      // no need to call set again — created already added
      console.log(`Created "${created.name}" and added "${activeSong.songname}"`);
    } else {
      addToPlaylist(favourites.id, activeSong);
      console.log(`Added "${activeSong.songname}" to "${favourites.name}"`);
    }
  }, [activeSong, playlists, addToPlaylist, createPlaylist]);

  // --- Audio element event handlers ---
  const handleLoadedMetadata = useCallback(() => {
    const audio = audioRef.current;
    if (audio) {
      setDuration(audio.duration || 0);
    }
  }, []);

  const handleTimeUpdate = useCallback(() => {
    const audio = audioRef.current;
    if (audio) setCurrentTime(audio.currentTime || 0);
  }, []);

  const handleEnded = useCallback(() => {
    // default behavior: play next
    playNextOrFallback();
  }, []); // playNextOrFallback defined later (see below)

  // fallback function for ended (we define it after playNext to avoid hoisting issues)
  const playNextOrFallback = useCallback(() => {
    if (songs && songs.length > 0) {
      // if activeSong exists, use playNext; else start first
      if (activeSong) {
        playNext();
      } else {
        setActiveSong(songs[0]);
        setIsPlaying(true);
      }
    } else {
      setIsPlaying(false);
    }
  }, [activeSong, songs, playNext]);

  // Attach/detach events on the hidden audio element
  useEffect(() => {
    const audio = audioRef.current;
    if (!audio) return;

    audio.addEventListener('loadedmetadata', handleLoadedMetadata);
    audio.addEventListener('timeupdate', handleTimeUpdate);
    audio.addEventListener('ended', playNextOrFallback);

    return () => {
      audio.removeEventListener('loadedmetadata', handleLoadedMetadata);
      audio.removeEventListener('timeupdate', handleTimeUpdate);
      audio.removeEventListener('ended', playNextOrFallback);
    };
  }, [handleLoadedMetadata, handleTimeUpdate, playNextOrFallback]);

  // When activeSong or isPlaying changes, ensure the audio element plays/pauses and src is correct
  useEffect(() => {
    const audio = audioRef.current;
    if (!audio) return;

    // set src if activeSong changed
    audio.src = activeSong ? activeSong.url : '';
    // reset time when new song selected
    if (activeSong) {
      audio.currentTime = 0;
      setCurrentTime(0);
      setDuration(0);
    }

    if (isPlaying) {
      const p = audio.play();
      if (p !== undefined) {
        p.then(() => setIsPlaying(true)).catch((err) => {
          console.warn('Autoplay prevented or play failed:', err);
          setIsPlaying(false);
        });
      }
    } else {
      // pause
      try { audio.pause(); } catch (e) { /* ignore */ }
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [activeSong, isPlaying]);

  // Expose context values
  const contextValue = {
    activeSong,
    isPlaying,
    currentTime,
    duration,
    songs,
    setSongs,
    playSong,
    togglePlayPause,
    playNext,
    playPrevious,
    handleSeek,
    playlists,
    addToPlaylist,
    createPlaylist,
    handleAddToPlaylist
  };

  return (
    <MusicPlayerContext.Provider value={contextValue}>
      {children}
      {/* Hidden audio element is managed by the provider so UI components call context functions only */}
      <audio ref={audioRef} style={{ display: 'none' }} />
    </MusicPlayerContext.Provider>
  );
};
