import React, { useState, useEffect, useMemo } from "react";
import { useNavigate } from "react-router-dom"; 
import "./Categories.css"; 
import "../components/Search.css"; // Reuse input styling for the search input
// NEW: Import the music player context to get dynamic playlists
import { useMusicPlayer } from '../MusicPlayerContext'; 

// A default cover image for categories that might not have enough songs
const DEFAULT_COVER = "https://placehold.co/100x100/303030/ffffff?text=Music";

// --- Configuration for Static Categories (Restructured for Grouping) ---
const STATIC_CATEGORY_GROUPS = {
    "Languages": [
        { id: "tamil", title: "🇮🇳 Top Tamil Songs", filter: (song) => song.language?.toLowerCase().includes('tamil') },
        { id: "english", title: "🌍 International English Hits", filter: (song) => song.language?.toLowerCase().includes('english') },
        { id: "hindi", title: "🎼 Bollywood Hindi Hits", filter: (song) => song.language?.toLowerCase().includes('hindi') },
    ],
    "Moods & Themes": [
        { id: "trending", title: "🔥 Trending Now Package", filter: (song) => song.songvariety?.includes('trending') },
        { id: "mass", title: "💥 Mass/Kuthu Hits", filter: (song) => song.songvariety?.includes('mass') },
        { id: "love", title: "💖 Pure Love Songs", filter: (song) => song.songvariety?.includes('romantic') || song.songvariety?.includes('love') },
        { id: "sad", title: "😢 Sad/Melody", filter: (song) => song.songvariety?.includes('sad') || song.songvariety?.includes('melancholy') },
        { id: "feel-good", title: "😊 Feel Good Hits", filter: (song) => song.songvariety?.includes('feel good') },
        { id: "vibe", title: "🧘 Vibe/Chill Songs", filter: (song) => song.songvariety?.includes('vibe') },
        { id: "party", title: "🥳 Party Starters", filter: (song) => song.songvariety?.includes('party') },
        { id: "dance", title: "💃 Dance Floor Hits", filter: (song) => song.songvariety?.includes('dance') },
        { id: "peppy", title: "⚡ Peppy & Energetic", filter: (song) => song.songvariety?.includes('peppy') },
        { id: "romantic-ballads", title: "💖 Romantic Ballads", filter: (song) => song.songvariety?.includes('romantic') || song.songvariety?.includes('ballad') },
        { id: "instrumental", title: "🎻 Classical Instrumental", filter: (song) => song.songvariety?.includes('instrumental') },
    ],
    "Artist Playlists": [
        { id: "anirudh", title: "🎤 Anirudh Ravichander Playlist", filter: (song) => song.artist?.includes('Anirudh Ravichander') },
        { id: "sidsriram", title: "🎶 Sid Sriram Collection", filter: (song) => song.artist?.includes('Sid Sriram') },
        { id: "chitra", title: "🎤 Chitra's Best Melodies", filter: (song) => song.artist?.includes('K. S. Chithra') || song.artist?.includes('Chinmayi') },
        { id: "hiphop-tamizha", title: "🎧 Hiphop Tamizha Swag", filter: (song) => song.artist?.includes('Hiphop Tamizha') },
        { id: "shreya-ghoshal", title: "🌟 Shreya Ghoshal Hits", filter: (song) => song.artist?.includes('Shreya Ghoshal') },
        { id: "gvprakash", title: "🎹 G. V. Prakash Kumar Songs", filter: (song) => song.artist?.includes('G.V. Prakash Kumar') },
        { id: "yuvan-raja", title: "👑 Yuvan Shankar Raja Classics", filter: (song) => song.artist?.includes('Yuvan Shankar Raja') },
    ]
};

// Flatten the static groups back into a single array for searching
const ALL_STATIC_CATEGORIES = Object.values(STATIC_CATEGORY_GROUPS).flat();

// --- Category Card Component (UNCHANGED) ---
const CategoryCard = React.memo(({ category, covers, onSelect }) => {
    return (
        <div 
            className="category-card" 
            onClick={() => onSelect(category)}
            aria-label={`View songs in ${category.title}`}
        >
            <div className="category-images">
                {Array.from({ length: 4 }).map((_, i) => (
                    <img 
                        key={i} 
                        src={covers[i] || DEFAULT_COVER} 
                        alt={`${category.title} album cover ${i + 1}`} 
                        onError={(e) => { e.target.onerror = null; e.target.src = DEFAULT_COVER; }}
                    />
                ))}
            </div>
            <h3>{category.title}</h3>
        </div>
    );
});


export default function Categories() {
    const [allSongs, setAllSongs] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [searchQuery, setSearchQuery] = useState(''); 
    const navigate = useNavigate(); 
    
    // NEW: Access dynamic playlists from context
    const { playlists } = useMusicPlayer();

    // Fetch all songs once from /songs.json (Mocked Implementation)
    useEffect(() => { 
        // Using the provided JSON data instead of fetching:
        const data = [
            // ... The full JSON data provided in the prompt goes here ...
            { "id": 1, "moviename": "Theeran", "songname": "Laali Laali", "artist": "Sathyaprakash D., Priya Hemesh", "language": "Tamil", "songvariety": [ "romantic", "love" ], "characters": "Karthi, Rakul Preet Singh", "url": "/music/Laali Laali.mp3", "duration": "0:30", "cover": "/covers/Laali Laali.jpg" },
            { "id": 2, "moviename": "Petta", "songname": "Marana Mass", "artist": "Anirudh Ravichander, S. P. Balasubrahmanyam", "language": "Tamil", "songvariety": [ "mass", "dance", "party" ], "characters": "Rajinikanth", "url": "/music/Marana Mass.mp3", "duration": "0:30", "cover": "/covers/Marana Mass.jpg" },
            { "id": 3, "moviename": "Kadhalar Dhinam", "songname": "Roja Roja", "artist": "S. P. Balasubrahmanyam, K. S. Chithra", "language": "Tamil", "songvariety": [ "romantic", "melody" ], "characters": "Kunal", "url": "/music/Roja Roja.mp3", "duration": "0:30", "cover": "/covers/Roja Roja.jpg" },
            { "id": 4, "moviename": "Ayan", "songname": "Vizhi Moodi", "artist": "Harris Jayaraj, Karthik", "language": "Tamil", "songvariety": [ "romantic", "love" ], "characters": "Suriya", "url": "/music/Vizhi Moodi.mp3", "duration": "0:30", "cover": "/covers/Vizhi Moodi.jpg" },
            { "id": 5, "moviename": "English Vinglish", "songname": "English Vinglish", "artist": "Amit Trivedi, Shilpa Rao", "language": "Hindi", "songvariety": [ "feel good", "peppy" ], "characters": "Sridevi", "url": "/music/English Vinglish.mp3", "duration": "0:30", "cover": "/covers/English Vinglish.jpg" },
            { "id": 6, "moviename": "Titanic", "songname": "My Heart Will Go On", "artist": "Celine Dion", "language": "English", "songvariety": [ "romantic", "ballad" ], "characters": "Leonardo DiCaprio", "url": "/music/My Heart Will Go On.mp3", "duration": "0:30", "cover": "/covers/My Heart Will Go On.jpg" },
            { "id": 7, "moviename": "Maaran", "songname": "Polladha Ulagam", "artist": "G.V. Prakash Kumar", "language": "Tamil", "songvariety": [ "trending", "vibe" ], "characters": "Dhanush", "url": "/music/Polladha Ulagam.mp3", "duration": "0:30", "cover": "/covers/Polladha Ulagam.jpg" },
            { "id": 8, "moviename": "Paiyaa", "songname": "En Kadhal Solla", "artist": "Yuvan Shankar Raja, Yuvan Shankar Raja", "language": "Tamil", "songvariety": [ "romantic", "love" ], "characters": "Karthi", "url": "/music/En Kadhal Solla.mp3", "duration": "0:30", "cover": "/covers/En Kadhal Solla.jpg" },
        ];

        setAllSongs(data); 
        setIsLoading(false);
    }, []); 

    // Navigation handler
    const handleCategorySelect = (category) => {
        // If it's a dynamic playlist, we navigate using its ID
        if (category.isDynamic) {
            console.log(`Navigating to dynamic playlist: ${category.title}`);
            navigate(`/playlist/${category.id}`);
        } else {
            // If it's a static category, navigate as before
            console.log(`Navigating to static category: ${category.title}`);
            navigate(`/playlist/${category.id}`);
        }
    };

    // Prepare categories, filtering by search query and adding cover data
    const groupedCategoriesWithCovers = useMemo(() => {
        const lowerCaseQuery = searchQuery.toLowerCase();
        const allCategoriesToSearch = [...ALL_STATIC_CATEGORIES, ...playlists];

        // Function to process a flat list of categories
        const processCategories = (categories, isDynamic = false) => {
            // Only filter by title/id for the search results (or if not dynamic)
            const searchableCategories = categories.filter(category => 
                category.title.toLowerCase().includes(lowerCaseQuery) ||
                category.id.includes(lowerCaseQuery) ||
                // Dynamic playlists should remain in search even if they don't have covers yet
                isDynamic 
            );

            // 2. Map and filter songs for the covers and detail data
            return searchableCategories.map(category => {
                // If it's a dynamic playlist from context, use its `songs` array for filtering
                const songsToFilter = isDynamic ? category.songs : allSongs.filter(category.filter);
                
                const filteredSongs = songsToFilter;
                
                const covers = filteredSongs
                    .slice(0, 4)
                    .map(song => song.cover)
                    .filter(cover => cover); 

                // Fill up to 4 covers with DEFAULT_COVER
                while (covers.length < 4) {
                    covers.push(DEFAULT_COVER);
                }

                return {
                    ...category,
                    isDynamic, // Mark it as dynamic
                    covers,
                    filteredSongs,
                    songCount: filteredSongs.length
                };
            });
        };

        // --- Grouping Logic ---
        
        // 1. Handle Search Query (Flatten everything)
        if (searchQuery) {
            // Determine if the category is dynamic for cover calculation
            const searchResults = allCategoriesToSearch.map(category => {
                const isDynamic = !!playlists.find(p => p.id === category.id);
                return processCategories([category], isDynamic)[0];
            }).filter(category => 
                category && 
                (category.title.toLowerCase().includes(lowerCaseQuery) || category.id.includes(lowerCaseQuery))
            );
            
            if (searchResults.length > 0) {
                 return { "Search Results": searchResults };
            } else {
                return {};
            }
        }

        // 2. Handle No Search Query (Grouped View)
        const result = {};

        // A. Add Dynamic Playlists First
        const dynamicPlaylistsProcessed = processCategories(playlists, true);
        if (dynamicPlaylistsProcessed.length > 0) {
            result["Your Playlists"] = dynamicPlaylistsProcessed;
        }

        // B. Add Static Categories
        for (const [groupName, categories] of Object.entries(STATIC_CATEGORY_GROUPS)) {
            const processed = processCategories(categories);
            if (processed.length > 0) {
                result[groupName] = processed;
            }
        }
        
        return result;

    }, [searchQuery, allSongs, playlists]); // Dependency on playlists ensures updates after song is added

    if (isLoading) {
        return <div className="loading">Loading Categories... 🎶</div>;
    }

    const allProcessedCategories = Object.values(groupedCategoriesWithCovers).flat();

    return (
        <div className="categories-page">
            <h1 className="page-title">Explore Playlists & Artists</h1>
            <div className="search-container">
                <input
                    type="text"
                    placeholder="Search Playlists or Artists (e.g., Peppy, My Favourites)"
                    className="search-input"
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    aria-label="Search categories"
                />
            </div>
            
            {allProcessedCategories.length === 0 && searchQuery && (
                <p className="no-results">No playlists match your search: "{searchQuery}"</p>
            )}

            {Object.entries(groupedCategoriesWithCovers).map(([groupName, categories]) => (
                // Only render the group if it contains categories
                categories.length > 0 && (
                    <React.Fragment key={groupName}>
                        <h2 style={{ color: '#fff', fontSize: '1.5rem', marginTop: '30px', marginBottom: '15px' }}>{groupName}</h2>
                        <div className="categories-grid">
                            {categories.map((category) => (
                                <CategoryCard
                                    key={category.isDynamic ? `dynamic-${category.id}` : category.id}
                                    category={category}
                                    covers={category.covers}
                                    onSelect={handleCategorySelect}
                                />
                            ))}
                        </div>
                    </React.Fragment>
                )
            ))}
        </div>
    );
}
