import { useEffect, useState, useRef, useCallback } from "react";
import "./SongCard.css";

// Helper function to format seconds into MM:SS
const formatTime = (seconds) => {
  if (isNaN(seconds) || seconds < 0) return "0:00";
  const minutes = Math.floor(seconds / 60);
  const remainingSeconds = Math.floor(seconds % 60);
  return `${minutes}:${remainingSeconds.toString().padStart(2, '0')}`;
};

export default function SongList() {
  const [songs, setSongs] = useState([]);
  const [activeSong, setActiveSong] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentTime, setCurrentTime] = useState(0);
  const [duration, setDuration] = useState(0);

  // Ref for the single, main audio element in the footer
  const audioRef = useRef(null);

  useEffect(() => {
    fetch("/songs.json")
      .then((res) => res.json())
      .then((data) => {
        const sortedSongs = data.sort((a, b) => a.id - b.id);
        setSongs(sortedSongs);
      })
      .catch((err) => console.error("Error fetching songs:", err));
  }, []);

  // --- Playback Controls Logic ---
  
  // Logic to move to the next/previous track
  const skipTrack = useCallback((direction) => {
    if (!activeSong || songs.length === 0) return;
    
    const currentIndex = songs.findIndex(s => s.id === activeSong.id);
    let newIndex = currentIndex + direction;

    if (newIndex < 0) {
      newIndex = songs.length - 1; // Loop to last song
    } else if (newIndex >= songs.length) {
      newIndex = 0; // Loop to first song
    }

    const nextSong = songs[newIndex];
    if (nextSong) {
      setActiveSong(nextSong);
      setIsPlaying(true);
    }
  }, [activeSong, songs]); 

  // --- Audio Event Handlers ---

  const handleLoadedMetadata = useCallback(() => {
    if (audioRef.current) {
      setDuration(audioRef.current.duration);
    }
  }, []);

  const handleTimeUpdate = useCallback(() => {
    if (audioRef.current) {
      setCurrentTime(audioRef.current.currentTime);
    }
  }, []);

  // Automatically skip to the next track when the current one ends
  const handleSongEnded = useCallback(() => {
    skipTrack(1); 
  }, [skipTrack]); 

  // Attach/Detach event listeners
  useEffect(() => {
    const audio = audioRef.current;
    if (!audio) return;

    audio.addEventListener('loadedmetadata', handleLoadedMetadata);
    audio.addEventListener('timeupdate', handleTimeUpdate);
    audio.addEventListener('ended', handleSongEnded);

    return () => {
      audio.removeEventListener('loadedmetadata', handleLoadedMetadata);
      audio.removeEventListener('timeupdate', handleTimeUpdate);
      audio.removeEventListener('ended', handleSongEnded);
    };
  }, [handleLoadedMetadata, handleTimeUpdate, handleSongEnded, activeSong]);


  // --- Play/Pause & Seek ---

  const playSong = (song) => {
    // If a different song is clicked, set it as active and play
    if (activeSong?.id !== song.id) {
      setActiveSong(song);
      setIsPlaying(true);
    } else {
      // If the same song is clicked, toggle play/pause
      togglePlayPause();
    }
  };

  const togglePlayPause = () => {
    const audio = audioRef.current;
    if (!audio) return;

    if (isPlaying) {
      audio.pause();
    } else {
      audio.play().catch(e => console.error("Play failed:", e));
    }
    setIsPlaying(!isPlaying);
  };

  const handleSeek = (e) => {
    const audio = audioRef.current;
    if (audio) {
      const newTime = e.target.value;
      audio.currentTime = newTime;
      setCurrentTime(newTime);
    }
  };
  
  // Handles auto-play after song load/change
  useEffect(() => {
      if (activeSong && audioRef.current && isPlaying) {
          audioRef.current.play().catch(e => {
              console.warn("Autoplay was prevented. User needs to interact.");
              setIsPlaying(false); 
          });
      }
  }, [activeSong, isPlaying]);


  return (
    <>
      <div className="song-list">
        {songs.map((song) => (
          <div 
            key={song.id} 
            className={`song-card ${activeSong?.id === song.id && isPlaying ? 'is-playing' : ''}`}
            // Clicking anywhere on the card triggers playSong
            onClick={() => playSong(song)}
          >
            <img src={song.cover} alt={song.songname} className="cover" />
            <h4>{song.songname}</h4>
            <p>{song.artist.split(',')[0]}</p>
            {/* The Custom Play Button DIV has been removed */}
          </div>
        ))}
      </div>

      {/* --- Full Player Fixed Footer (when a song is active) --- */}
      {activeSong && (
        <div className="player-footer">
          {/* Audio element for playback */}
          <audio 
            ref={audioRef}
            key={activeSong.id} 
            src={activeSong.url}
            preload="auto"
          />
          
          {/* Song Info */}
          <div className="song-info player-info">
            <img 
              src={activeSong.cover} 
              alt={activeSong.songname} 
              className="player-cover" 
            />
            <div>
              <h4>{activeSong.songname}</h4>
              <p>{activeSong.artist.split(',')[0]}</p>
            </div>
          </div>

          {/* Controls and Progress */}
          <div className="progress-container">
            {/* Custom Controls */}
            <div className="player-controls">
                <button onClick={() => skipTrack(-1)}>&lt;&lt;</button>
                <button onClick={togglePlayPause}>
                    {isPlaying ? '❚❚' : '▶'}
                </button>
                <button onClick={() => skipTrack(1)}>&gt;&gt;</button>
            </div>

            {/* Time and Seek Bar */}
            <span className="progress-time">{formatTime(currentTime)}</span>
            <input
              type="range"
              min="0"
              max={duration || 0}
              value={currentTime}
              className="progress-bar"
              onChange={handleSeek}
            />
            <span className="progress-time">{formatTime(duration)}</span>
          </div>

          {/* Volume and Other Options (Placeholder) */}
          <div style={{ flex: 1, textAlign: 'right' }}>
            {/* E.g., Volume slider or other icons */}
          </div>
        </div>
      )}
    </>
  );
}
