import { useEffect, useState } from "react";
import axios from "axios";
import { useAuth } from "../context/AuthContext";
import SongCard from "../components/SongCard";
import Search from "../components/Search";
import Pagination from "../components/Pagination";

export default function Dashboard() {
  const { user, logout } = useAuth();
  const [songs, setSongs] = useState([]);
  const [filtered, setFiltered] = useState([]);
  const [playlist, setPlaylist] = useState([]);
  const [error, setError] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const songsPerPage = 4;

  useEffect(() => {
    axios.get("/songs.json")
      .then((res) => {
        setSongs(res.data);
        setFiltered(res.data);
      })
      .catch(() => setError("Failed to load songs"));
  }, []);

  const handleSearch = (query) => {
    const result = songs.filter(
      (s) =>
        s.title.toLowerCase().includes(query.toLowerCase()) ||
        s.artist.toLowerCase().includes(query.toLowerCase()) ||
        s.language.toLowerCase().includes(query.toLowerCase())
    );
    setFiltered(result);
    setCurrentPage(1);
  };

  const handleAddToPlaylist = (song) => {
    if (!playlist.find((s) => s.id === song.id)) {
      setPlaylist([...playlist, song]);
    }
  };

  // Pagination
  const indexOfLast = currentPage * songsPerPage;
  const indexOfFirst = indexOfLast - songsPerPage;
  const currentSongs = filtered.slice(indexOfFirst, indexOfLast);

  return (
    <div className="dashboard">
      <header>
        <h1>Welcome, {user?.name || user?.email}</h1>
        <button onClick={logout}>Logout</button>
      </header>

      <Search onSearch={handleSearch} />

      {error && <p className="error">{error}</p>}

      <div className="songs-list">
        {currentSongs.map((song) => (
          <SongCard key={song.id} song={song} onAddToPlaylist={handleAddToPlaylist} />
        ))}
      </div>

      <Pagination
        total={filtered.length}
        perPage={songsPerPage}
        currentPage={currentPage}
        setCurrentPage={setCurrentPage}
      />

      <h2>Your Playlist</h2>
      <ul>
        {playlist.map((s) => (
          <li key={s.id}>{s.title} - {s.artist}</li>
        ))}
      </ul>
    </div>
  );
}
