// src/pages/Dashboard.jsx
import { useEffect, useState } from "react";
import { searchDeezer } from "../deezerService";
import SongCard from "../components/SongCard";
import Search from "../components/Search";

export default function Dashboard() {
  const [songs, setSongs] = useState([]);
  const [loading, setLoading] = useState(false);

  const fetchSongs = async (query = "Arijit Singh") => {
    setLoading(true);
    const results = await searchDeezer(query);
    setSongs(results);
    setLoading(false);
  };

  useEffect(() => {
    fetchSongs(); // Default search
  }, []);

  return (
    <div>
      <h2 style={{ textAlign: "center", margin: "20px 0" }}>ðŸŽµ Discover Songs</h2>
      <Search onSearch={fetchSongs} />
      {loading ? (
        <p style={{ textAlign: "center" }}>Loading songs...</p>
      ) : (
        <div style={{ display: "flex", flexWrap: "wrap", justifyContent: "center" }}>
          {songs.map((song) => (
            <SongCard
              key={song.id}
              song={song}
              onAddToPlaylist={(s) => console.log("Added:", s)}
            />
          ))}
        </div>
      )}
    </div>
  );
}
