import React, { useEffect } from "react";
import "./SongCard.css";
// 1. Correct Import: This path is based on your file structure (components/SongCard to context/MusicPlayerContext)
import { useMusicPlayer } from '../context/MusicPlayerContext'; 

// All local state, refs, and playback logic have been correctly removed.

export default function SongCard() {
    // 2. Use the context hook to access global player state and functions.
    const { songs, activeSong, isPlaying, playSong, setSongs } = useMusicPlayer();
    
    // --- Data Fetching ---
    // This useEffect runs once to fetch data and populate the global 'songs' state via setSongs.
    useEffect(() => {
        // Ensure this path '/songs.json' is correct for your project
        fetch("/songs.json")
          .then((res) => res.json())
          .then((data) => {
            const sortedSongs = data.sort((a, b) => a.id - b.id);
            // 3. Call setSongs from the context to update the global song list
            setSongs(sortedSongs); 
          })
          .catch((err) => console.error("Error fetching songs:", err));
    }, [setSongs]); // setSongs is included in the dependency array as good practice.

    return (
        <>
            <div className="song-list">
                {songs.map((song) => (
                    <div 
                        key={song.id} 
                        // The class name uses global state (activeSong, isPlaying) for visual feedback
                        className={`song-card ${activeSong?.id === song.id && isPlaying ? 'is-playing' : ''}`}
                        // 4. Clicking the card calls the global playSong function
                        onClick={() => playSong(song)}
                    >
                        <img src={song.cover} alt={song.songname} className="cover" />
                        <h4>{song.songname}</h4>
                        <p>{song.artist.split(',')[0]}</p>
                    </div>
                ))}
            </div>
            {/* 5. Player JSX is correctly omitted here. */}
        </>
    );
}
